var pHa = global.get("pHLVL");
var pHb = global.get("pHgoal");
var pHgoal = global.get("pHgoal");
var pHLVL=global.get("pHLVL");
var WL = global.get("WL");  // [L]
var Macid = 0.0043;  //85% phosphoric acid [14.8 mol/L]
var Mbase = 0.05; //45% potassium hydroxide [11.7 mol/L]
var Ha=Math.pow(10,-pHa);   // [mol/L]
var Hb=Math.pow(10,-pHb);   // [mol/L]
var dH=Math.abs(Hb-Ha);     // [mol/L]
var pHmax = pHgoal+0.2;
var pHmin = pHgoal-0.1;
var base_dose;
var acid_dose;
var dose;
var doseL;
var doseml;

var msgX = { payload: 0 };
var msg2 = { payload:"pH:"+pHLVL };
var msg3 = { payload:"pHgoal: "+pHgoal };
var msg4 = { payload: doseml };
var msg5 = { payload:"dH: "+dH };
var msg6 = { payload:"WL: "+WL+"L" };
var msg7 = { payload:"Ha: "+Ha };
var msg8 = { payload:"Hb: "+Hb };
var msg9 = { payload:"Dose: "+doseml+" ml" };

if (pHa>pHmax){
  doseL=dH/Macid*WL;
  doseml=(Math.round(doseL*1000*1000)/1000).toFixed(3);
  msg4.payload=doseml;
  msg8.payload="Dose acid:"+doseml+"ml";
  return [ [msg4],[msgX],[msg2,msg3,msg5,msg8] ];
}

if (pHa<pHmin){
  doseL=dH/Mbase*WL;
  doseml=(Math.round(doseL*1000*1000)/1000).toFixed(3);
  msg4.payload=doseml;
  msg8.payload="Dose alkali:"+doseml+"ml";
  return [ [msgX],[msg4],[msg2,msg3,msg5,msg8] ];
}

else {
  return [ [msgX],[msgX],[msg2,msg3,msg5] ];
}

